function checkcode3(valuecode)
 {
  
	var  dataString = 'val='+valuecode;
	
	$.ajax({
		type: "GET",
		url: "<?php echo APP_ROOT_URL; ?>checkcode.php",
		data: dataString,
		cache: true,
		success: function(value)
			{  
				if(value==0){
					$("#s_code5").val(value);	
					$("#security_code").focus();
					return false;
				} else {
				    $("#s_code5").val(value);
				    return false;
				}
				
			}
		});
 }
 
